mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

update setmeal_dish
copies = #{copies}, price = #{price}, name = #{name}, dish_id = #{dishId},
where setmeal_id = #{setmealId}

mapper.select:
select s.id, category_id, s.name, price, s.status, description, image,
       s.update_time, c.name as category_name from setmeal s, category c

    s.category_id = c.id
    and s.name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and s.status = #{status}
